var tcpServer = global.get("tcpServer");
if (tcpServer === undefined)    tcpServer = {};
var retMsg;
var retStatus;
var ip;

if (msg.status !== undefined){  // Cas d'un status
    ip = msg.status.ip.split(":")[3];
    if (msg.status.event == "connect"){
        tcpServer[ip] = msg.status._session;
        retMsg = {  ip : ip};
    }
    if (tcpServer[ip] !== undefined && msg.status.event == "disconnect" && tcpServer[ip].id == msg.status._session.id)
        delete tcpServer[ip];
    retStatus = {payload : msg.status};
    // retStatus = {payload : {text : msg.status.text}};
}
else{   // Cas d'un msg
    ip = msg.ip.split(":")[3];
    if (tcpServer[ip] !== undefined && msg._session.id == tcpServer[ip].id)
        retMsg = {  ip : ip,
                    payload : JSON.parse(msg.payload)};
}
global.set("tcpServer",tcpServer);
return [retMsg,retStatus];

