var ip = msg.ip;
if (ip != env.get("IP"))
    return;

var msgSend;
var tcpServer = global.get("tcpServer");
var watering = global.get("watering");
if (watering === undefined) watering = {};

if (watering[ip] === undefined){
    msgSend = {payload : "system.all=1\nsystem.go=1\n",
            _session : tcpServer[ip]};
    watering[ip] = {};
}
else{
    if (msg.payload === undefined){ //Cas d'une connexion
        var trame = "";
        if (watering[ip].cmd !==undefined){
            for (var key in watering[ip].cmd)
                trame = trame + key + "=" + watering[ip].cmd[key] + "\n";
            delete watering[ip].cmd;
        }
        trame = trame + "system.go=1\n";
        msgSend = { payload :  trame,
                    _session : tcpServer[ip]};
    }
    else{ //Si message surement un retour des cmd donc purge
        delete watering[ip].cmd;
    }
}
global.set("watering",watering);
return [msgSend,msg];

