var msgPower = {payload:true};
var msgColor;
var msgToggle;
var msgBackTV;
var globe;
var cubeSide = flow.get("cubeSide");

switch (msg.payload.gesture){
    case 1: //Shake 3 times
        msgToggle = {payload:true};
        break;
    case 2: //Fall
        break;
    case 3: //Change Side
    case 4: //Opposite Side
        flow.set("cubeSide", Math.trunc(msg.payload.buttonevent/1000));
        break;
    case 5: //Slide
        flow.set("cubeSide", Math.trunc(msg.payload.buttonevent/1000));
        break;
    case 6: //Beat 2 times
    case 7: //Rotate Clockwise
    case 8: //Rotate AntiClockWise
        switch (cubeSide){
            case 1:
                break;
            case 2:
                globe = flow.get("globe");
                if (globe.on)
                    msgColor = {payload:((globe.hue + msg.payload.buttonevent + 65535) % 65535) }
                break;
            case 3:
                if (msg.payload.gesture == 7)
                    msgBackTV = {payload:true};
                if (msg.payload.gesture == 8)
                    msgBackTV = {payload:false};
                break;
        }
        break;
}
return [msgPower, msgToggle, msgColor, msgBackTV];

