var powerAvg = flow.get("powerAVG");
if (isNaN(powerAvg)) powerAvg = 0;
var powerAvgCount = flow.get("powerAVGCount");
if (isNaN(powerAvgCount)) powerAvgCount = 0;

if (msg.payload == "reset"){
    powerAvg = 0;
    powerAvgCount = 0;
}
else{
    powerAvg = (powerAvg*powerAvgCount/(powerAvgCount+1))+(msg.payload/(powerAvgCount+1));
    powerAvgCount = powerAvgCount + 1;
}

flow.set("powerAVGCount", powerAvgCount);
flow.set("powerAVG", powerAvg);

return {payload:{ "avg" : powerAvg,
                    "count" : powerAvgCount,
